'use strict';

exports.json = function(object)
{
    let spacer = `    `,
        json = `{\n${spacer}`;

    for (var key in object)
    {
        if (!object.hasOwnProperty(key))
        { continue; }

        let value = object[key];

        if (typeof value === typeof '')
        { json += `"<key>${key}</key>": "<value contenteditable='true'>${value}</value>",\n${spacer}`; }

        else
        {
            json += `"<key>${key}</key>": [\n<array contenteditable='true'>${spacer}`;
            value.forEach(function(value)
            { json += `${spacer}"<value>${value}</value>",\n${spacer}`; });

            json = json.replace(/\,[\n\s]*$/, '') + `</array>\n${spacer}],\n${spacer}`;
        }
    }

    json = json.replace(/\,[\n\s]*$/, '') + '\n}';
    return json;
}